package bastanteo;

public class Rol {

	private String nombre;
	private String opcion;

	public Rol(String nombre, String opcion) {
		// TODO Auto-generated constructor stub
		this.nombre = nombre;
		this.opcion = opcion;
	}

	public String getNombre() {
		// TODO Auto-generated method stub
		return nombre;
	}

	public String getOpcion() {
		// TODO Auto-generated method stub
		return opcion;
	}

}